local T, C, L, G = unpack(Tukui)local DataTextPosition = function(f, t, o)	local left = TukuiInfoLeft	local right = TukuiInfoRight	local mapleft = TukuiMinimapStatsLeft		if o == 1 then		t:ClearAllPoints()		t:SetParent(left)		t:SetHeight(left:GetHeight())		t:SetPoint("LEFT", left, 30, 0)		t:SetPoint('TOP', left)		t:SetPoint('BOTTOM', left)	elseif o == 2 then		t:ClearAllPoints()		t:SetParent(left)		t:SetHeight(left:GetHeight())		t:SetPoint('TOP', left)		t:SetPoint('BOTTOM', left)	elseif o == 3 then		t:ClearAllPoints()		t:SetParent(left)		t:SetHeight(left:GetHeight())		t:SetPoint("RIGHT", left, -30, 0)		t:SetPoint('TOP', left)		t:SetPoint('BOTTOM', left)	elseif o == 4 then		t:ClearAllPoints()		t:SetParent(right)		t:SetHeight(right:GetHeight())		t:SetPoint("LEFT", right, 30, 0)		t:SetPoint('TOP', right)		t:SetPoint('BOTTOM', right)	elseif o == 5 then		t:ClearAllPoints()		t:SetParent(right)		t:SetHeight(right:GetHeight())		t:SetPoint('TOP', right)		t:SetPoint('BOTTOM', right)	elseif o == 6 then		t:ClearAllPoints()		t:SetParent(right)		t:SetHeight(right:GetHeight())		t:SetPoint("RIGHT", right, -30, 0)		t:SetPoint('TOP', right)		t:SetPoint('BOTTOM', right)			elseif o == 7 then		t:ClearAllPoints()		t:SetParent(mapleft)		t:SetHeight(mapleft:GetHeight())		t:SetPoint('TOP', mapleft, 0, 0)		t:SetPoint('BOTTOM', mapleft)		t:SetPoint('BOTTOM', mapleft)		else			f:Hide()		t:Hide()	endendlocal datatext = {	"Guild",	"Friends",	"Gold",	"FPS",	"System",	"Bags",	"Gold",	"Time",	"Durability",	"Heal",	"Damage",	"Power",	"Haste",	"Crit",	"Avoidance",	"Armor",	"Currency",	"Hit",	"Mastery",	"MicroMenu",	"Regen",	"Talent",	"CallToArms",}for _, data in pairs(datatext) do	local t = "TukuiStat"	local frame = _G[t..data]	local text = _G[t..data.."Text"]	if frame and frame.Option then		DataTextPosition(frame, text, frame.Option)		text:SetFont(unpack(T.Fonts.dFont.setfont))	endendG.DataText.Gold:SetScript( "OnMouseDown", function( self, btn )	if( btn == "RightButton" and IsShiftKeyDown() ) then		local myPlayerRealm = GetCVar( "realmName" )		local myPlayerName  = UnitName( "player" )		TukuiData.gold = {}		TukuiData.gold[myPlayerRealm] = {}		TukuiData.gold[myPlayerRealm][myPlayerName] = GetMoney()	else		ToggleAllBags()	endend)